mapper.delete:
DELETE
FROM board
WHERE bno = #{bno}
  and writer = #{writer}

mapper.update:
UPDATE board
SET title   = #{title}
  , content = #{content}
  , up_date = now()
WHERE bno = #{bno}
  and writer = #{writer}

UPDATE board
SET comment_cnt = comment_cnt + #{cnt}
WHERE bno = #{bno}

UPDATE board
SET view_cnt = view_cnt + 1
WHERE bno = #{bno}
